"use strict";
$(document).ready(function () {
  var e = document.getElementById("zero_user_traffic_chart"),
    t = parseInt(KTUtil.css(e, "height"));
  if (e) {
    var a = e.getAttribute("data-kt-chart-color"),
      s = KTUtil.getCssVariableValue("--bs-gray-800"),
      o = KTUtil.getCssVariableValue("--bs-" + a),
      r = KTUtil.getCssVariableValue("--bs-" + a + "-light");
    $.ajax({
      dataType: "json",
      url: "/user/ajax_data/chart/traffic",
      method: "POST",
      data: {},
      success: function (e) {
        l.updateSeries([{ name: e[0].name, data: e }]);
      },
    });
    var l = new ApexCharts(e, {
      series: [],
      chart: {
        type: "area",
        height: t,
        toolbar: { show: !1 },
        zoom: { enabled: !1 },
        sparkline: { enabled: !0 },
      },
      plotOptions: {},
      legend: { show: !1 },
      dataLabels: { enabled: !1 },
      fill: { type: "solid", opacity: 1 },
      stroke: { curve: "smooth", show: !0, width: 3, colors: [o] },
      xaxis: {
        axisBorder: { show: !1 },
        axisTicks: { show: !1 },
        labels: { show: !1, style: { colors: s, fontSize: "12px" } },
        crosshairs: {
          show: !1,
          position: "front",
          stroke: { color: "#E4E6EF", width: 1, dashArray: 3 },
        },
        tooltip: {
          enabled: !1,
          formatter: void 0,
          offsetY: 0,
          style: { fontSize: "12px" },
        },
      },
      yaxis: { labels: { show: !1, style: { colors: s, fontSize: "12px" } } },
      states: {
        normal: { filter: { type: "none", value: 0 } },
        hover: { filter: { type: "none", value: 0 } },
        active: {
          allowMultipleDataPointsSelection: !1,
          filter: { type: "none", value: 0 },
        },
      },
      tooltip: {
        style: { fontSize: "12px" },
        y: {
          formatter: function (e) {
            return e + "GB";
          },
        },
      },
      colors: [o],
      markers: { colors: [o], strokeColor: [r], strokeWidth: 3 },
    });
    l.render();
  }
});
var KTZeroUserWalletChart = (function () {
  var e = function () {
    var e = document.getElementById("zero_user_wallet_chart");
    if (e) {
      parseInt(KTUtil.css(e, "height"));
      var t = JSON.parse($("#user_balance").html()),
        a = JSON.parse($("#user_commission").html()),
        s = {
          series: [t, a],
          chart: { fontFamily: "inherit", type: "donut", width: 250 },
          plotOptions: {
            pie: {
              donut: { size: "50%", labels: { value: { fontSize: "10px" } } },
            },
          },
          colors: [
            KTUtil.getCssVariableValue("--bs-success"),
            KTUtil.getCssVariableValue("--bs-primary"),
          ],
          stroke: { width: 0 },
          labels: ["可使用金额", "可提现佣金"],
          legend: { show: !1 },
          fill: { type: "false" },
        };
      new ApexCharts(e, s).render();
    }
  };
  return {
    init: function () {
      e();
    },
  };
})();
KTUtil.onDOMContentLoaded(function () {
  KTZeroUserWalletChart.init();
});
